// src/components/Card.js

import React from "react";

const Card = ({ item }) => {
  return (
    <div className='box'>
      <img src={item.urlToImage} alt={item.title} />
      <div className='text'>
        <h1>{item.title}</h1>
        <div className='category'>{item.source.name}</div>
        {/* DiÄŸer haber bilgilerini buraya ekleyebilirsiniz */}
      </div>
    </div>
  );
};

export default Card;
